$cs-Messenger:
{"index":"12","group":"z_boot","title":"Messenger","sfile":"","script":"(function(global) {

  define("#Messenger", [], function(require, exports, module) {
    // An instance
    var messenger = null;

    function Messenger() {
      this.port = chrome.runtime.connect();
      this.funcCallback = {};
      this.scriptCallback = {};
      this.domain = null;

      this.init();
    }

    Messenger.prototype.init = function() {
      // Get domain of current page
      var domainMatch = location.href.match(/^[\w-]+:\/*\[?([\w\.:-]+)\]?(?::\d+)?/); 
      this.domain = domainMatch ? domainMatch[0] : "";

      // register on message posted event handler
      window.addEventListener("message", function(event) {
        //console.log("Message received in Messenger", event, ". Current domain is", messenger.domain);

        // We only accept messages from current page
        if (!event.data || event.origin != messenger.domain)
          return;

        // invoked when injected.js send a message after invoked function returns
        else if (event.data.type == "JST-callMethodResponse") {
          //console.log("Messenger get response from function", event.data.func);

          if (event.data.callback) {
            var returnValue = JSON.parse(event.data.returnValue);
            var callback = messenger.funcCallback[event.data.callback];
            callback.call(global, returnValue);

            delete callback;
            delete messenger.funcCallback[event.data.callback];
          }
        }

        // UNUSED
        else if (event.data.type == "FROM_PAGE") {
          console.log("Content script function invocation request received: " + event.data.text);
          console.log(event);
          port.postMessage(event.data.text);
        }
      }, false);
    };

    Messenger.prototype.guid = function() {
      return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = Math.random()*16|0, v = c == 'x' ? r : (r&0x3|0x8);
        return v.toString(16);
      });
    };

    Messenger.prototype.loadContentScript = function(csName, callback) {
      var callbackID = this.guid(), msgData = {name:csName};
      if (callback) {
        this.scriptCallback[callbackID] = callback;
        msgData["callback"] = callbackID;
      }
      chrome.runtime.sendMessage( 
        {tabid: INFO.tabid, method:"InjectContentScriptAsScriptNode", 
         data:JSON.stringify(msgData) } );
    };

    Messenger.prototype.onScriptLoaded = function(callbackID) {
      if (callbackID) {
        //console.log("Script is loaded, callback id is:", callbackID, "and all script callback is", this.scriptCallback);
        var callback = this.scriptCallback[callbackID];
        callback.call(global);

        delete callback;
        delete this.scriptCallback[callbackID];
      }
    };

    // args should be an array, and returnCallback is a function taking one argument.
    Messenger.prototype.invokeFunction = function(funcName, args, returnCallback) {
      if (!funcName.startsWith("__JSTricks_Injected_"))
        throw new Error("Function name has to start with __JSTricks_Injected_");

      var msg = {type: "JST-callMethod", func:funcName}; 

      if (!args) {
        args = [];
      }
      msg["args"] = JSON.stringify(args);

      if (returnCallback) {
        var callbackID = this.guid();
        msg["callback"] = callbackID;
        this.funcCallback[callbackID] = returnCallback;
      }

      window.postMessage(msg, this.domain);
    };


    // Export symbols
    messenger = new Messenger();
    window.__JSTricks_Messenger = messenger;

    return messenger;  
  });

}) (this);"}

