{"index":"10","group":"z_lib","title":"Novel","sfile":"","script":"define("#Novel", ["jquery", "msgbox", "#LibBase", "#Messenger"], function(require, exports, module) {
  var $ = require("jquery");
  var log = require("msgbox");  // log prints a message in console and show a message box.
  var libBase = require("#LibBase");

  function Novel() {
    this.config = this.getConfig("Novel");
  }
  Novel.prototype = libBase;

  // Prototypes for Novel
  Novel.prototype.prefetch = function (target) {
    //next = $('a:contains("下一"):eq(0)')[0];
    next = $(target)[0];
    if (next) {
      link = next.href;
      href = $(next).attr('href');
      prefetch1 = '<link rel="prefetch" href="' + link + '" />'; // For Firefox browser
      prefetch2 = '<link rel="prerender" href="' + link + '" />'; // For Google Chrome browser
      $('body').append(prefetch1);
      $('body').append(prefetch2);
      var node = $("a[href='"+href+"']");
      var style = node.attr("style");
      if (style == undefined)
        style = "";
      if ($(next).children().length > 0) {
        style += "background-color: rgba(247,223,29,1);";
      } else {
        style += `color: blue; 
                color: blue;
                border: 1px solid rgba(124,122,12,1);
                /* cursor: pointer; */
                background-color: rgba(247,223,29,1);
                background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fcf1b4), color-stop(50%,#fce448), color-stop(51%,#f4d300), color-stop(100%,#fbec8b));
                box-shadow: rgba(0,0,0,0.2) 0 -1px 2px inset,rgba(255,255,255,1) 0 1px 2px inset;
                /* padding: 3px 10px; */
                text-shadow: rgba(255,255,255,0.6) 0 1px;`;
    }

      node.attr("style", style);
      log("[Novel] Prefetch nodes for " + target + " have been inserted.");
    } else {
      log("[Novel] Target '" + target + "' does not matche any node.");
    }
  };

  Novel.prototype.defaultPrefetch = function(target1, target2) {
    this.prefetch(target1);
    this.prefetch(target2);
  };

  Novel.prototype.urlMatches = function(url, type) {
    var urlPatternStr = url;
    if (type == "Wildcard") {
      urlPatternStr = urlPatternStr.replace(/\./g, "\\.").replace(/\?/g, "\\?").replace(/\+/g, "\\+").replace(/\=/g, "\\=").replace(/\&/g, "\\&").replace(/\*/g, ".*");
    } else if (type == "Regexp") {
      urlPatternStr = urlPatternStr.replace(/\./g, "\\.").replace(/\=/g, "\\=").replace(/\&/g, "\\&");
    } else if (type == "Raw Regexp") {
    }
    var urlPattern = new RegExp(urlPatternStr);
    var result = urlPattern.test(location.href);
    var status = result ? "<font color='red'>PASSED</font>" : "FAILED";
    log(`[Novel] ${type} test ${status} for '${urlPatternStr}'`);
    return result;
  };

  Novel.prototype.replaceText = function (target, pattern, replacement, method) {
    node = $(target);
    text = node.html();
    text = text.replace(pattern, replacement);

    if (!method || method == 1) {
      node.html(text);
    } else if (method == 2) {
      var parentNode = node.parent();
      //node.replaceWith("<div>" + text + "</div>");
      node.detach();
      $("<div>" + text + "</div>").appendTo(parentNode);
    }
    var msg = "[Novel] Text replacement for " + target + " ["+pattern+" --> "+replacement+"] is done.";
    log(msg, true);
  };

  Novel.prototype.addThousands = function (selector) {
    pattern = /(\d+)(\d{3})(\D|$)/g;
    replacement = function(str, sub1, sub2, sub3, n) {
      pattern = /(\d+)(\d{3})(\D|$)/g;
      return sub1.replace(pattern, replacement) + ',' + sub2 + sub3;
    }
    replaceNum = function(index, ele) {
      var text = ele.innerText;
      ele.innerText = text.replace(pattern, replacement);
    };

    $(selector).each(replaceNum);

    log("[JScript Tricks - Novel] Thousands seperators for " + selector + " have been added.");
  };

  Novel.prototype.markTableColumn = function(table, 
                                    columnIndexStart, columnIndexTo, className) {
    var index1 = columnIndexStart - 1;
    var index2 = columnIndexTo + 1;
    $(table + " tr").find(`td:eq(${columnIndexStart})`).addClass(className);
    $(table + " tr").find(`td:lt(${index2}):gt(${index1})`).addClass(className);

    log(`[Novel] Mark columns [${columnIndexStart}-${columnIndexTo}] as "${className}" for table ${table}. `);
  };

  Novel.prototype.unbindEvent = function (target, e) {
      $(target).unbind(e);
  };

  Novel.prototype.enableRightClick = function() {
    // call __JSTricks_Injected_ method defined in injected.js
    window.postMessage({ type: "JST-callMethod", func: "enableRightClick" }, "*");

    log(`[Novel] Rightclick is enabled.`);
  };

  // end of Novel prototypes

  return new Novel();
});
"}

